css:
/*pc*/
@media (min-width: 601px){ 
.header__logo {
    max-width: 300px;
    max-height: 100px;
}
  body{font-family: 'Poppins', sans-serif;}

  .header__buttons {margin-top: 0px;}

  .header__search-btn{color: #ccb97c;}
    .top-bar {
      background: #ffffff;}
  .top-bar__nav a { font-size:14px;}
  .top-bar__nav{color: #1d4056;}
  .menu__item{color: #263d4e;}
  .menu__list a.focus, .menu__list a:focus, .menu__list a:hover {
    background: #d6c68c;
}
  .header__nav a:hover {color: #dbca93; background:#fff;}
  .header {
    background: #ffffff;
    border-bottom: 1px solid #e1e1e1;}
  .footer__store {
    color: var(--secondary); font-size: 23px;
    font-family: 'Poppins', sans-serif;
    font-weight: 700;
}
  p{ font-size: 16px;font-family: 'Poppins', sans-serif;color:#1d4056;}
  .footer__links {
    font-size: 18px;
}
 .pay-icon--pix {
    display: none;
}.pay-icon {
  width: 80px;}
  .footer__title {
    font-size: 20px;
    font-weight: 700;
    margin-bottom: var(--spacer-2);
    display: block;
    color: #1d4056;
    outline: none;
}
  .list-unstyled{  font-size: 18px;
}.stamps img {
  max-width: 100px;}
  .footer__credits{font-size:14px;}
  .ecom-credits {
    padding: 1.5rem;
    background: #e8e8e8;
    text-align: left;
    font-size: 12px;
    color: #101010;
}
  .btn-primary {
    background-color: #1d4056;
    border-color: #1d4056;
}
  .product__name{font-size: 21px;}
  .product__sku{font-size:15px;}
  .prices--big .prices__value {
    font-size: 30px; color: #1d4056;}
  .prices__value{font-size: 16px;}
  .prices__installments{font-size: 14px;color:#22a480:}

  .share-network-whatsapp{display:none;}
  .share-network-facebook{display:none;}
  .share-network-twitter{display:none;}
  .share-network-telegram{display:none;}
  .share-network-pinterest{display:none;}
  .share-network-email {display:none;}
  .share-network-sms{display:none;}
  .card-header {
    padding: .75rem 1.25rem;
    color: #fff;
    background-color: rgb(34 164 128);
    border-bottom: 1px solid rgb(0 0 0 / 0%);font-size:15px;
}
.fa-chevron-down{
   color: #ffffff;}
  #user-button{color: #00506c;background:#fff;}
  .fa-user{color: #00506c;background:#fff;}
}
.btn-primary:hover{
    background-color: #22a480;
    border-color: #22a480;
}
.breadcrumb-item{font-size:15px;}
.breadcrumb-item.active {font-size:15px;
    color:#22a480;
}
.page-title__head{font-size:30px;}
.page-title__short-description{font-size:18px;}
.html-clearfix{font-family: 'Poppins', sans-serif;
  border: 1px solid #ccc;
    padding: 10px 30px 30px 10px;}
.product__info {font-size:16px;}
.products-carousel__title{ color:#00506c;}
.products-carousel__title:before{height: 2px;background: #c6b56f;}
.my-0{font-size:30px;}
.small {
    position: absolute;
    top: -25px;
    right: -25px;
}.mr-2 {
    font-size: 0.5rem;
}.product-card__offer-stamp {
  background-color: #0e0e0e;}
}
